    public void autoID(){
        try {
            Class.forName("com.mysql.jdbc.Driver");
            conn = DriverManager.getConnection("jdbc:mysql://localhost:3306/bank?","root","Isr@1995");
            //conn = DriverManager.getConnection("jdbc:mysql://localhost:3325/bank?","root","");
            Statement s = conn.createStatement();
            ResultSet rs = s.executeQuery("select MAX(Acc) from account");
            rs.next();
            rs.getString("MAX(Acc)");
            if(rs.getString("MAX(Acc)") == null)
            {
                //jLabel17.setText("");
                txtacc.setText("62216400002");
            }
            else
            {
                long Acc = Long.parseLong(rs.getString("MAX(Acc)").substring(2,rs.getString("MAX(Acc)").length()));
                Acc++;
                 //jLabel17.setText("" + String.format("%d", Acc));
                 txtacc.setText("62" + String.format("%05d", Acc));
                
            }
            
         
        } catch (ClassNotFoundException ex) {
            Logger.getLogger(Account.class.getName()).log(Level.SEVERE, null, ex);
        } catch (SQLException ex) {
            Logger.getLogger(Account.class.getName()).log(Level.SEVERE, null, ex);
        }
    
    }
   
    
    
