    public void RandomPIN(){
        Random ra = new Random();
        txtpin.setText(""+ra.nextInt(8400+1));
     
    }
    
    
    
    public void Bal(){
        String sql = "insert into balances(Name,Acc,MICR_No,Balance)values(?,?,?,?)";
        //String sql = "insert into account(Name,Acc,MICR_No,Balance)values(?,?,?,?)";
        try{
            pst=conn.prepareStatement(sql);
            pst.setString(1, txtname.getText());
            //pst.setString(2, jLabel17.getText());
            pst.setString(2, txtacc.getText());
            pst.setString(3, txtmicr.getText());
            pst.setString(4, txtamount.getText());
            pst.execute();
        
        }
        catch(Exception e){
            JOptionPane.showMessageDialog(null, e);
        
        }
    
    }
    
    
    
    
    
