    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        
        
        String sql = "insert into account(Acc, Name,DOB,Pin,Acc_Type,Nationality,Caste,MICR_No,Gender,Mob,Address,Sec_Q,Sec_A,Balance) values(?,?,?,?,?,?,?,?,?,?,?,?,?,?)";
                try{
                    pst = conn.prepareStatement(sql);
                    //pst.setString(1, jLabel17.getText());
                    pst.setString(1,txtacc.getText());
                    pst.setString(2,txtname.getText());
                    pst.setString(3,((JTextField)txtdob.getDateEditor().getUiComponent()).getText());
                    pst.setString(4,txtpin.getText());
                    pst.setString(5,(String) accComboBox.getSelectedItem());
                    pst.setString(6,(String) txtComboBox.getSelectedItem());
                    pst.setString(7,txtcaste.getText());
                    pst.setString(8,txtmicr.getText());
                    
                    jRadioButton1.setActionCommand("Male");
                    jRadioButton2.setActionCommand("Female");
                    pst.setString(9,buttonGroup1.getSelection().getActionCommand());
                    
                    pst.setString(10,txtmob.getText());
                    pst.setString(11,txtaddress.getText());
                    
                    pst.setString(12,(String) SQComboBox.getSelectedItem());
                    
                    pst.setString(13,txtsa.getText());
                    pst.setString(14,txtamount.getText());
                
                    pst.execute();
                    JOptionPane.showMessageDialog(null, "Congrats\n Account has been Created");
                    Bal();
                           
                   
                }catch(Exception e){
                    JOptionPane.showMessageDialog(null, e);
                
                }
    }//GEN-LAST:event_jButton1ActionPerformed

